# setting the Status from Issued to Returned in Library Transaction Doctype when the Article is Returned

if doc.return_date and doc.status == "Issued":
    doc.status = "Returned"

if doc.return_date:
    article = frappe.get_doc("Article", doc.article)
    if article.status == "Issued":
        article.status = "Available"
        article.save()
